<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Game</title>
    @vite('../resources/css/app.css')
    <style>
        /* Vanilla CSS for flip card */
        .card {
            perspective: 800px;
            width: 200px;
            height: 200px;
        }

        .card__content {
            transform-style: preserve-3d;
            transition: transform 1s;
        }

        .card--flipped .card__content {
            transform: rotateY(180deg);
        }

        .card__front,
        .card__back {
            backface-visibility: hidden;
            position: absolute;
            top: 0;
            bottom: 0;
            left: 0;
            right: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            border-radius: 15px;
        }

        /* .card__front {
            background-color: #f4a261;
        } */

        .card__back {
            /* background-color: #2a9d8f; */
            transform: rotateY(180deg);
        }
    </style>
</head>

